definition.script:
@Library('buildit')
def LOADED = true

pomVerion = ''
repositoryUrl = "git@github.com:electroma/jenkins-pipeline-libraries.git"
credentialsId = "github-credentials"

node {
stage('prepare') {
    sh ('rm -rf *')
    sh ('rm -rf .git')
    git poll: false, changelog: false, url: repositoryUrl, credentialsId: credentialsId, branch: "master"
}

gitUtils = new git()
uiInst = new ui()

stage 'branch selection'
    def branch = input(message: "Select Branch", parameters: [uiInst.dropdown("branch",
        "Branch", {gitUtils.listBranches(repositoryUrl, "master", credentialsId)})], submitter: null)

    sh("git checkout ${branch}")

    pipeline = readFile("./pipelines/meta-pipeline.groovy")
    evaluate(pipeline)
}
